import React, { Component, useState, useEffect } from "react";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import Legend from "./Legend";
import HeatmapLayer from "react-leaflet-heatmap-layer"

function Header(){
  return(
  <header className = 'header-Class'>
    <p>Testing out React-Leaflet</p>
  </header>
  );
}

//This function will fetch the data that is being sent from the backend
function GetData(){
  const [data, setdata] = useState({
		body: []
	});
	// Using useEffect for single rendering
	useEffect(() => {
		// Using fetch to fetch the api from
		// flask server it will be redirected to proxy
		fetch("/data").then((res) =>
			res.json().then((data) => {
				// Setting a data from api
				setdata({
					body: data
				});
			})
		);
	}, []);
  var arrData = data.body;
  var totalArray = [];
  //go through the datapoints and process them into a format that the plugin can 
  //understand
  for(let i =0; i < arrData.length; i++){
    //make sure the data point isn't a dud
    if((arrData[i]["lat"] !== "")){
      var depthDict = [];
      depthDict.push(parseFloat(arrData[i]["lat"]));
      depthDict.push(parseFloat(arrData[i]["lon"]));
      depthDict.push(arrData[i]["depth"]);
      totalArray.push(depthDict)
    }
  }
  //return the array
  return(totalArray);
}

class MapLeaflet extends Component {
  render() {
    var stateVar = {
      lat: 30.4515,
      lng: -91.1871,
      zoom: 10,
    }
    const position = [stateVar.lat, stateVar.lng]
    var data = [[-37.90585105, 175.4453463833, "1273"],
    [-37.9064188833, 175.4441556833, "1258"],
    [-37.90584715, 175.4463564333, "1279"],
    [-37.9033391333, 175.4244005667, "1078"],
    [-37.9061991333, 175.4492620333, "1309"],
    [-37.9058955167, 175.4445613167, "1261"],
    [-37.88888045, 175.39146475, "734"],
    [-37.8950811333, 175.41079175, "928"],
    [-37.88909235, 175.3922956333, "740"],
    [-37.8889259667, 175.3938591667, "759"],
    [-37.8876576333, 175.3859563833, "687"],
    [-37.89027155, 175.3973178833, "778"],
    [-37.8864473667, 175.3806136833, "631"],
    [-37.9000262833, 175.4183242167, "1012"],
    [-37.90036495, 175.4189457, "1024"]]
    //var data = processData(GetData().body);
    console.log(data);
    
    return(
      //create the map and its tile layer
      <Map center={position} zoom={stateVar.zoom}>
      <TileLayer
        attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <Legend />
    </Map>
    );
  }
}

export default MapLeaflet;
